    /*
     * Editing a pedido
     */
    function edit($id_pedido)
    {   
        // check if the pedido exists before trying to edit it
        $data['pedido'] = $this->Pedido_model->get_pedido($id_pedido);
        
        if(isset($data['pedido']['id_pedido']))
        {
            if(isset($_POST) && count($_POST) > 0)     
